select lat,longt, (3963.191 * ACOS( (SIN(PI()* 40.7383040 /180)*SIN(PI()*lat/180)) + (COS(PI()* 40.7383040 /180)*cos(PI()*lat/180)*COS(PI() * longt/180-PI()* -73.99319 /180)) )) AS distance  FROM user  WHERE 1=1  AND 3963.191 * ACOS(SIN(PI()* 40.7383040 /180)*SIN(PI() * lat/180)) + (COS(PI()* 40.7383040 /180)*cos(PI()*lat/180)*COS(PI() * longt/180-PI()* -73.99319 /180)) ) < = 1.5  ORDER BY 3963.191 * ACOS( (SIN(PI()* 40.7383040 /180)*SIN(PI()*lat/180)) + (COS(PI()* 40.7383040 /180)*cos(PI()*lat/180)*COS(PI() * longt/180-PI()* -73.99319 /180)) )